class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<long long, long long>> pq1, pq2;

        for(int i = 0; i < horizontalCut.size(); i++){
            pq1.push({horizontalCut[i], i});
        }
        for(int i = 0; i < verticalCut.size(); i++){
            pq2.push({verticalCut[i], i});
        }

        int hCut = 1, vCut = 1; long long ans = 0;
        while(!pq1.empty() && !pq2.empty()){
            if(pq1.top().first > pq2.top().first){
                int ele = pq1.top().first;
                int index = pq1.top().second;
                pq1.pop();

                ans += vCut * horizontalCut[index];
                hCut += 1;
            }
            else{
                int ele = pq2.top().first;
                int index = pq2.top().second;
                pq2.pop();

                ans += hCut * verticalCut[index];
                vCut += 1;
            }
        }

        while(!pq1.empty()){
            int ele = pq1.top().first;
            int index = pq1.top().second;
            pq1.pop();

            ans += vCut * horizontalCut[index];
            hCut += 1;
        }
        while(!pq2.empty()){
            int ele = pq2.top().first;
            int index = pq2.top().second;
            pq2.pop();

            ans += hCut * verticalCut[index];
            vCut += 1;
        }


        return ans;
    }
};
