class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
          vector<pair<int,int>>d;
        for(auto a:a)d.push_back({a,0});
        for(auto b:b)d.push_back({b,1});
        sort(d.rbegin(),d.rend());
        vector<int>v(2);
        long long ans=0;
        for(int i=0;i<d.size();i++){
            auto [cost,id]=d[i];
            v[id]++;
            ans+=1ll*(v[id^1]+1)*cost;   
        }
        return ans;
    }
};
