class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long horizontalSum=0,verticalSum=0;
        long long res=0;
        for(auto &c:horizontalCut)horizontalSum+=c;
        for(auto &c:verticalCut)verticalSum+=c;
        int i=1,j=1;
        sort(horizontalCut.begin(),horizontalCut.end(),[](long long a,long long b){
            return a>b;
        });
        sort(verticalCut.begin(),verticalCut.end(),[](long long a,long long b){
            return a>b;
        });
        // cout<<horizontalCut[0]<<endl;
        while(i<=m-1 && j<=n-1){
            if(horizontalCut[i-1] > verticalCut[j-1]){
                res+=horizontalCut[i-1]*j;
                horizontalSum-=horizontalCut[i-1];
                i++;
            }else{
                res+=verticalCut[j-1]*i;
                verticalSum-=verticalCut[j-1];
                j++;
            }
        }
        while(i<=m-1){
            res+=horizontalCut[i-1]*j;
            i++;
        }
        while(j<=n-1){
            res+=verticalCut[j-1]*i;
            j++;
        }
        return res;
    }
};
