class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(), hc.end(), std::greater<int>());
    sort(vc.begin(), vc.end(), std::greater<int>());
   int hi = 0, vi = 0;
    long long hp = 1, vp = 1;
    long long tc = 0;
 while (hi < hc.size() && vi < vc.size()) {
        if (hc[hi] > vc[vi]) {
            tc += static_cast<long long>(hc[hi++]) * vp;
            hp++;
        } else {
            tc += static_cast<long long>(vc[vi++]) * hp;
            vp++;
        }
    }
    while (hi < hc.size()) {
        tc += static_cast<long long>(hc[hi++]) * vp;
    }
     while (vi < vc.size()) {
        tc += static_cast<long long>(vc[vi++]) * hp;
    }

    return tc;
    }
};
