class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        long long i=1,j=1;
        long long ans=0;
        int ci=n-2,cj=m-2;
        while(ci>=0&&cj>=0){
            if(h[cj]>v[ci]){
                ans+=(h[cj]*i);
                cj--;
                j++;
            }
            else{
                ans+=(v[ci]*j);
                ci--;
                i++;
            }
        }
        while(ci>=0){
            ans+=(v[ci]*j);
            ci--;
        }
        while(cj>=0){
            ans+=(h[cj]*i);
            cj--;
        }
        return ans;
    }
};
