class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cut = []

        for i, c in enumerate(horizontalCut):
            heapq.heappush(cut, (-c, 'h'))

        for i, c in enumerate(verticalCut):
            heapq.heappush(cut, (-c, 'v'))

        res = 0

        h = 1
        v = 1

        while cut:
            c, d = heapq.heappop(cut)

            if d == 'h':
                res += -c * v
                h += 1
            else:
                res += -c * h
                v += 1
        return res
