struct Cut {
    int cost;
    bool isHorizontal;
};

bool compareCuts(const Cut &a, const Cut &b) {
    return a.cost > b.cost;
}
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<Cut> cuts;

    // Add horizontal cuts
    for (int i = 0; i < horizontalCut.size(); ++i) {
        cuts.push_back({horizontalCut[i], true});
    }

    // Add vertical cuts
    for (int j = 0; j < verticalCut.size(); ++j) {
        cuts.push_back({verticalCut[j], false});
    }

    // Sort cuts by cost in descending order
    sort(cuts.begin(), cuts.end(), compareCuts);

    int horizontalSegments = 1;
    int verticalSegments = 1;
    long long totalCost = 0;

    for (const auto &cut : cuts) {
        if (cut.isHorizontal) {
            totalCost += static_cast<long long>(cut.cost) * verticalSegments;
            horizontalSegments++;
        } else {
            totalCost += static_cast<long long>(cut.cost) * horizontalSegments;
            verticalSegments++;
        }
    }

    return totalCost;
    }
};
