class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        // 如何使得开销最小？
        // 行之间或列之间不会相互影响
        // 每次行切使得列切开销++，反之亦然
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);
        int hor = horizontalCut.length-1, ver = verticalCut.length-1;
        int horAcc = 1, verAcc = 1;
        long sum = 0;
        while (hor >= 0 || ver >= 0) {
            if (hor >= 0 && (ver < 0 || horizontalCut[hor] > verticalCut[ver])) {
                sum += horizontalCut[hor] * verAcc;
                hor--;
                horAcc++;
            } else {
                sum += verticalCut[ver] * horAcc;
                ver--;
                verAcc++;
            }
        }
        return sum;
    }
}
