class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
                priority_queue<int>vr;
        priority_queue<int>hr;
        for(auto x:hor) hr.push(x);
        for(auto x:ver) vr.push(x);
        long long horline= 1,verline=1;
        long long  res=0;
        while(!hr.empty() || !vr.empty()){
            if(!hr.empty() && !vr.empty() ){
                if(hr.top()>vr.top()){
                    res+=(verline*hr.top());
                    hr.pop();
                    horline++;
                }
                else{
                    res+=(horline*vr.top());
                    verline++;
                    vr.pop();
                }
            }
            else if(!vr.empty()){
                res+=(horline*vr.top());
                vr.pop();
            }
            else if(!hr.empty()) {
                res+=(verline*hr.top());
                hr.pop();
            }
        }
        return res;
    }
};
