class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int,int>>com(m+n);
        for(int i=0;i<m-1;i++){
            com[i]={h[i],0};
        }
        for(int i=0;i<n-1;i++){
            com[m+i]={v[i],1};
        }
        sort(com.rbegin(),com.rend());
        ll vr=1;
        ll hr=1;
        ll cost=0;
        for(auto [c,t]:com){
            if(t==0){
                cost+=(hr*c);
                vr++;
            }
            else{
                cost+=(vr*c);
                hr++;
            }
        }
        return cost;        
    }
};
