class Solution {
public:
    static bool comp(pair<int,int>&a,pair<int,int>&b){
    return a.first>b.first;
}
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>>cuts;
        for(auto it:horizontalCut){
            cuts.push_back({it,0});
        }
        for(auto it:verticalCut){
            cuts.push_back({it,1});
        }

        sort(cuts.begin(),cuts.end(),comp);
        int vertical=1;
        int horizontal=1;
        long long cost=0;
        for(auto it:cuts){
            if(it.second==0){
                cost+=(horizontal*it.first);
                vertical++;
            }else{
                cost+=(vertical*it.first);
                horizontal++;
            }
        }
        return cost;
    }
};
