class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long res = 0;
        int hc = 1,vc =1;
        sort(h.begin(),h.end(),greater<int>());
        sort(v.begin(),v.end(),greater<int>());
        int i=0,j=0;
        while(i<h.size() && j<v.size()){
            if(h[i]>v[j]){
                res+=(h[i]*vc);
                hc++;
                i++;
            }
            else{
                res+=(v[j]*hc);
                vc++;
                j++;
            }
        }
        while (i < h.size()) {
            res += h[i] * vc;
            i++;
        }
        while (j < v.size()) {
            res += v[j] * hc;
            j++;
        }
        return res;
    }
};
