class comparator
{

public:
    bool operator()(pair<int, string> &a, pair<int, string> &b)
    {
        return a.first > b.first;
    }
};
class Solution
{
public:
    long long minimumCost(int m, int n, vector<int> &horizontalCut, vector<int> &verticalCut)
    {
        vector<pair<int, string>> ref;

        for (int i = 0; i < m - 1; i++)
        {
            ref.push_back({horizontalCut[i], "hor"});
        }

        for (int i = 0; i < n - 1; i++)
        {
            ref.push_back({verticalCut[i], "ver"});
        }
        sort(ref.begin(), ref.end(), comparator());
        int cols = 1;
        int rows = 1;
        long long ans = 0;
        int size = ref.size();
        for (int i = 0; i < size; i++)
        {
            if (ref[i].second == "hor")
            {
                ans += (ref[i].first * cols);
                rows++;
            }
            else
            {
                ans += (ref[i].first * rows);
                cols++;
            }
        }
        return ans;
    }
};
