class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long ans = 0;
        long long row = 1, col = 1;

        multiset<pair<int, char> > pick;

        for(auto e : horizontalCut){
            pick.insert({e, 'r'});
        }

        for(auto e : verticalCut){
            pick.insert({e, 'c'});
        }

        for (auto itr = pick.rbegin(); itr != pick.rend(); ++itr) {
        if (itr->second == 'r') {
            ans += itr->first * row;
            col++;
        } else {
            ans += itr->first * col;
            row++;
        }
    }
        return ans;

    }
};
