class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin(),hc.end());
        sort(vc.begin(),vc.end());


        long long hp = 1, vp = 1;
        long long cost = 0;

        while (!hc.empty() && !vc.empty()) {
            if (hc.back() > vc.back()) {
                cost += (long long)hc.back() * vp;
                hc.pop_back();
                hp++;
            } else {
                cost += (long long)vc.back() * hp;
                vc.pop_back();
                vp++;
            }
        }

        while (!hc.empty()) {
            cost += (long long)hc.back() * vp;
            hc.pop_back();
        }

        while (!vc.empty()) {
            cost += (long long)vc.back() * hp;
            vc.pop_back();
        }

        return cost;
    }
};
