class Solution {
public:
    struct node{
        int h,x;
        bool operator<(const node&b)const{
            return h<b.h;
        }
    };
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        int mm=h.size();
        int nn=v.size();
        int na=1,nb=1;
        priority_queue<node>p;
        for(int nu:h)p.push({nu,1});
        for(int nu:v)p.push({nu,2});
        long long ans=0;
        while(p.size()){
            node tt=p.top();p.pop();
            if(tt.x==1){
                na++;
                ans+=1LL*tt.h*nb;
            }else{
                nb++;
                ans+=1LL*tt.h*na;
            }

        }
        return ans;
    }
};
