class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& ho, vector<int>& ve) 
    {
        sort(ho.begin(),ho.end(),greater<int>());
        sort(ve.begin(),ve.end(),greater<int>());
        int h=1,v=1;
        long long ans=0;
        int i=0,j=0;
        while (i<ho.size() && j<ve.size()) 
        {
            if(ho[i]>ve[j]) 
            {
                ans+=ho[i]*v;
                h++;
                i++;
            } 
            else
            {
                ans+=ve[j]*h;
                v++;
                j++;
            }
        }
        while(i<ho.size()) 
        {
            ans+=ho[i]*v;
            i++;
        }
        while(j<ve.size()) 
        {
            ans+=ve[j]*h;
            j++;
        }
        return ans;

    }
};
