class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long x=1;//ngang horizontalCut
        long long y=1;//doc verticalCut
        long long cost=0;
        sort(horizontalCut.begin(),horizontalCut.end(),greater());
        sort(verticalCut.begin(),verticalCut.end(),greater());
        int i=0;
        int j=0;
        while(i<m-1||j<n-1){
            if(i==m-1){

                // cost+=x*verticalCut[j];
                // j++;
                cost+=x*accumulate(verticalCut.begin()+j,verticalCut.end(),0);
                break;
            }
            else if(j==n-1){
                // cost+=y*horizontalCut[i];
                // i++;
                cost+=y*accumulate(horizontalCut.begin()+i,horizontalCut.end(),0);
                break;
            }
            else if(horizontalCut[i]>=verticalCut[j]){

                cost+=y*horizontalCut[i];
                x++;
                i++;
            }
            else{
                cost+=x*verticalCut[j];
                y++;
                j++;
            }

        }
        return cost;


    }
};
