class Solution {
    #define ll long long 
    #define pll pair<ll, ll>
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        ll ans=0;
        ll mc = 1;
        ll nc = 1;
        vector<pll> vc;
        for(int i=0; i<m-1; i++){
            vc.push_back({h[i], 0});
        }
        for(int i=0; i<n-1; i++){
            vc.push_back({v[i], 1});
        }

        sort(vc.begin(), vc.end());

        for(int i=m+n-3; i>=0; i--){
            if(vc[i].second==0){
                nc++;
                ans += vc[i].first*mc;
            }
            else{
                mc++;
                ans += vc[i].first*nc;
            }

        }

        return ans;

    }
};
