class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        long long cost = 0;
        sort(hc.rbegin(), hc.rend());
        sort(vc.rbegin(), vc.rend());

        long long h = 0, v = 0;
        long long h_pieces = 1, v_pieces = 1;

        while(h < hc.size() && v < vc.size()) {
            if(hc[h] >= vc[v]) {
                cost += (long long)hc[h++] * v_pieces;
                h_pieces++;
            } else {
                cost += (long long)vc[v++] * h_pieces;
                v_pieces++;
            }
        }

        while(h < hc.size()) {
            cost += (long long)hc[h++] * v_pieces;
            h_pieces++;
        }

        while(v < vc.size()) {
            cost += (long long)vc[v++] * h_pieces;
            v_pieces++;
        }

        return cost;
    }
};
