class Solution {
    public long minimumCost(int m, int n, int[] h, int[] v) {
        Arrays.sort(h);
        Arrays.sort(v);
        long ans = 0;
        int hi = h.length-1;
        int vi = v.length-1;
        long hf = 1, vf = 1;
        while (hi >= 0 || vi >= 0) {
            if (hi >= 0 && vi >= 0) {
                if (h[hi] > v[vi]) {
                    ans += h[hi]*vf;
                    hf++;
                    hi--;
                } else {
                    ans += v[vi]*hf;
                    vf++;
                    vi--;
                }
            } else if (hi >= 0) {
                ans += h[hi]*vf;
                hi--;
            } else {
                ans += v[vi]*hf;
                vi--;
            }
        }
        return ans;
    }
}
