class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        hvp = [1, 1] # ver, hor
        costs = []
        for i, c in enumerate(horizontalCut):
            costs.append((c, i, 0)) # cost, ind, horiz/vertic
        for i, c in enumerate(verticalCut):
            costs.append((c, i, 1))

        costs.sort(reverse=True)

        res = 0
        for c, ind, hv in costs:
            res += c * hvp[hv]
            hvp[(hv+1)%2] += 1
        return res
