class Solution {
public:

    long long minimumCost(int m, int n, vector<int>& H, vector<int>& V) {
        sort(H.rbegin(), H.rend());
        sort(V.rbegin(), V.rend());
        long long cost=0;
        int hpiece=1, vpiece=1;
        int i=0,j=0;
        while (i < H.size() && j < V.size()) {
            if (H[i] > V[j]) {
                cost += (long long)H[i] * vpiece;
                hpiece++;
                i++;
            } else {
                cost += (long long)V[j] * hpiece;
                vpiece++;
                j++;
            }
        }
        while (i < H.size()) cost += (long long)H[i++]*vpiece;
        while (j < V.size()) cost+=(long long)V[j++]*hpiece;
        return cost;
    }
};
