class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
             long long int res=0,v=0,h=0;
        vector<pair<long long int,pair<long long int,char>>>vv;
        for(auto it:horizontalCut)vv.push_back({it,{n*it,'H'}});
        for(auto it:verticalCut)vv.push_back({it,{m*it,'V'}});
        sort(vv.begin(),vv.end(),greater<pair<long long int,pair<long long int,char>>>());
        for(auto it:vv){
            long long int a=it.second.first,aa=it.first; char b=it.second.second;
            if(b=='V'){v++; res+=(aa)*(h+1);} else {h++; res+=(aa)*(v+1);}
        }
        return res;
    }
};
