class Solution {
public:
    typedef long long ll;
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        vector<pair<ll,char>>v;
        for(auto it:hc){
            v.push_back({it,'h'});
        }
        for(auto it: vc){
            v.push_back({it,'v'});
        }

        sort(v.rbegin(), v.rend());

        ll h1 = 1;
        ll v1 = 1;
        ll ans = 0;

        for(auto it:v){
            ll cost = it.first;
            char type = it.second;
            if(type=='h'){
                ans += cost*v1;
                h1++;
            } 
            else{
                ans += cost*h1;
                v1++;
            }
        }
        return ans;
    }
};
