class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.begin(), hCut.end());
        sort(vCut.begin(), vCut.end());
        long long ans = 0;
        for(int i = 0; i<hCut.size(); i++){
            long long x = upper_bound(vCut.begin(), vCut.end(), hCut[i]) - vCut.begin();
            ans+= (long long)(hCut[i]*(n - x));
        }
        for(int i = 0; i<vCut.size(); i++){
            long long  x = lower_bound(hCut.begin(), hCut.end(), vCut[i]) - hCut.begin();
            ans+= (long long) (vCut[i]*(m - x));
        }
        return ans; 
    }
};
