class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        long long ct=1,ctt=1;
        int i=0,j=0;
        while(i<h.size() && j<v.size()){
            if(h[i]>v[j]){
                ans+=(h[i]*ctt);
                ct++;i++;
            }
            else{
                ans+=(v[j]*ct);
                ctt++;j++;
            }
        }
        long long res=0;
        while(i<h.size()){
            res+=h[i];
            i++;
        }
        ans+=(res*ctt);
        res=0;
        while(j<v.size()){
            res+=v[j];
            j++;
        }
        ans+=(res*ct);
        return ans;
    }
};
