typedef long long ll;
class Solution {
public:
    priority_queue<pair<ll,ll>> pq;
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        for (auto i: horizontalCut) pq.push({i, 0});
        for (auto i: verticalCut) pq.push({i, 1});
        ll ans = 0;
        ll h = 0, v = 0;
        while (pq.size()) {
            if (pq.top().second == 0) {
                ans += (v + 1) * pq.top().first;
                ++h;
            } else {
                ans += (h + 1) * pq.top().first;
                ++v;
            }
            pq.pop();
        }
        return ans;
    }
};
