function minimumCost(m: number, n: number, horizontalCut: number[], verticalCut: number[]): number {
    let hCuts = horizontalCut.length;
    let vCuts = verticalCut.length;
    horizontalCut.sort((a, b) => b - a);
    verticalCut.sort((a, b) => b - a);
    let hIdx = 0, vIdx = 0;

    let hBlocks = 1, vBlocks = 1

    let ans = 0;

    while (hIdx < hCuts && vIdx < vCuts) {
        if (horizontalCut[hIdx] >= verticalCut[vIdx]) {
            ans += horizontalCut[hIdx] * vBlocks;
            hBlocks++;
            hIdx++;
        } else {
            ans += verticalCut[vIdx] * hBlocks;
            vBlocks++;
            vIdx++;
        }
    }
    while (hIdx < hCuts) {
        ans += horizontalCut[hIdx] * vBlocks;
        hIdx++;
    }

    while (vIdx < vCuts) {
        ans += verticalCut[vIdx] * hBlocks;
        vIdx++;
    }

    return ans;
};
