class Solution {
public: 
    #define ll long long

  long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
    priority_queue<pair<ll, ll>> pq;
    vector<ll> as;
    as.push_back(1);
    as.push_back(1);
    for(auto &i: a) pq.push({i, 0});
    for(auto &i: b) pq.push({i, 1});
    ll q = 0;
    while(!pq.empty()) {
      auto p = pq.top();
      pq.pop();
      q += as[(p.second ^ 1)] * p.first;
      as[p.second]++;
    }
    return q;
  }
};
