class Solution {
#define ll long long
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        ll hcnt{1},vcnt{1},cost{};
        sort(hc.rbegin(),hc.rend());
        sort(vc.rbegin(),vc.rend());

        ll i{},j{};

        while(i<hc.size() && j<vc.size()){
            if(hc[i]>vc[j]){
                cost+=hc[i]*vcnt;
                hcnt++;
                i++;
            }
            else {
                cost+=vc[j]*hcnt;
                vcnt++;
                j++;
            }
        }
        while(j<vc.size()){
            cost+=vc[j]*hcnt;
            j++;
        }

        while(i<hc.size()){
            cost+=hc[i]*vcnt;
            i++;
        }
        return cost;
    }
};
