class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.rbegin(), hCut.rend());
        sort(vCut.rbegin(), vCut.rend());

        long long h = 0, v = 0;
        long long hPieces = 1, vPieces = 1;
        long long ans = 0;

        while (h < hCut.size() && v < vCut.size()) {
            if (hCut[h] > vCut[v]) {
                ans += hCut[h] * vPieces;
                hPieces++;
                h++;
            } else {
                ans += vCut[v] * hPieces;
                vPieces++;
                v++;
            }
        }

        // Add remaining horizontal cuts
        while (h < hCut.size()) {
            ans += hCut[h] * vPieces;
            h++;
        }

        // Add remaining vertical cuts
        while (v < vCut.size()) {
            ans += vCut[v] * hPieces;
            v++;
        }

        return ans;
    }
};
