class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<pair<int,int>>pq;//stores...cuts and number of times the operation has been applied..
        for(int i=0;i<m-1;i++){
            pq.push({h[i],1});
        }
        for(int j=0;j<n-1;j++){
            pq.push({v[j],0});
        }
        long long int hcut=1,vcut=1,ans=0;
        while(!pq.empty()){
            int ele=pq.top().first;
            int type=pq.top().second;
            pq.pop();
            if(type==1){
                hcut++;
                ans+=(vcut)*ele;
            }
            else {
                vcut++;
                ans+=(hcut)*ele;
            }

        }
        return ans;
    }
};
