static bool comp(int &a, int &b){
    return a > b;
}
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        swap(n, m);
        sort(h.begin(), h.end(), comp);
        sort(v.begin(), v.end(), comp);
        long long res = 0;
        int vDiv = 0, hDiv = 0;
        int i = 0, j = 0;
        while(i < n-1 && j < m-1){
            if(h[i] < v[j]){
                res += (hDiv+1)*v[j];
                vDiv++;
                j++;
            } else if (h[i] > v[j]){
                res += (vDiv+1)*h[i++];
                hDiv++;
            } else {
                if(vDiv > hDiv){
                    res += (hDiv+1)*v[j++];
                    vDiv++;
                } else {
                    res += (vDiv+1)*h[i++];
                    hDiv++;
                }
            } 
        }


        while(i < n-1){
            res += (vDiv+1)*h[i++];
            hDiv++;

        }
        while(j < m-1){
            res += (hDiv+1)*v[j++];
            vDiv++;

        }

        return res;
    }
};
