#define ll long long int
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vs) {

        vector<int> hp(hc);
        vector<int> vp(vs);
        sort(vp.begin(), vp.end(), greater<int>());
        sort(hp.begin(), hp.end(), greater<int>());
        long long res = 0;

        int smart = 1, dumb = 1;

        int i = 0, j = 0;
        while (i < m - 1 && j < n - 1) {
            if (hp[i] <= vp[j]) {
                 res += (vp[j])*1ll * smart;
                dumb++;
                j++;


            } else {
                 res += (hp[i])*1ll * dumb;
                smart++;
                i++;
            }
        }
        while (j < n - 1) {
            res += (vp[j++]) *1ll* smart;
        }
        while (i < m - 1) {
            res += (hp[i++]) *1ll* dumb;
        }


        return res;
    }
};
