class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        horizontalCut.sort()
        verticalCut.sort()
        res = 0
        l,r = 1,1
        while horizontalCut or verticalCut:
            if horizontalCut and verticalCut:
                if horizontalCut[-1] > verticalCut[-1] or (horizontalCut[-1] == verticalCut[-1] and l > r):
                    res += horizontalCut.pop()*r 
                    l += 1
                else:
                    res += verticalCut.pop()*l
                    r += 1
            elif horizontalCut:
                while horizontalCut:
                    res += horizontalCut.pop()*r 
            else:
                while verticalCut:
                    res += verticalCut.pop()*l 
        return res
