class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        // Sort horizontal and vertical cuts in descending order
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        int horizontal_pieces = 1;
        int vertical_pieces = 1;
        long long total_cost = 0;

        int h_index = 0;
        int v_index = 0;

        while (h_index < horizontalCut.size() && v_index < verticalCut.size()) {
            if (horizontalCut[h_index] > verticalCut[v_index]) {
                total_cost += horizontalCut[h_index] * vertical_pieces;
                horizontal_pieces++;
                h_index++;
            } else {
                total_cost += verticalCut[v_index] * horizontal_pieces;
                vertical_pieces++;
                v_index++;
            }
        }

        while (h_index < horizontalCut.size()) {
            total_cost += horizontalCut[h_index] * vertical_pieces;
            horizontal_pieces++;
            h_index++;
        }

        while (v_index < verticalCut.size()) {
            total_cost += verticalCut[v_index] * horizontal_pieces;
            vertical_pieces++;
            v_index++;
        }

        return total_cost;

    }
};
