class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        cuts = [[c1, 'h'] for c1 in horizontalCut]
        cuts.extend([[c2, 'v'] for c2 in verticalCut])
        cuts.sort()
        numV, numH = 1, 1
        res = 0
        while cuts:
            currCut = cuts.pop()
            if currCut[1] == 'v':
                res += numH * currCut[0]
                numV += 1
            else:
                res += numV * currCut[0]
                numH += 1
        return res
