class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Map<Integer, Integer> hmap = new HashMap<>();
        Map<Integer, Integer> vmap = new HashMap<>();
        for(int i = 0; i<m-1; i++){
            add(hmap, horizontalCut[i]);
        }
        for(int i = 0; i<n-1; i++){
            add(vmap, verticalCut[i]);
        }
        long hCount = 1, vCount = 1;
        long ans = 0L;
        for(int i = 1000; i>0; ){
            if(hmap.containsKey(i) && vmap.containsKey(i)){
                if(vCount<=hCount){
                    ans += i*vCount;
                    hCount++;
                    remove(hmap, i);
                }
                else{
                    ans += i*hCount;
                    vCount++;
                    remove(vmap, i);
                }
            }
            else if(hmap.containsKey(i)){
                ans += i*vCount;
                hCount++;
                remove(hmap, i);
            }
            else if(vmap.containsKey(i)){
                ans += i*hCount;
                vCount++;
                remove(vmap, i);
            }
            else{
                i--;
            }
        }
        return ans;
    }

    public void add(Map<Integer, Integer> map, int val){
        map.put(val, map.getOrDefault(val, 0)+1);
    }

    public void remove(Map<Integer, Integer> map, int val){
        map.put(val, map.get(val)-1);
        if(map.get(val)==0) map.remove(val);
    }
}
