class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(begin(hc),end(hc));
        sort(begin(vc),end(vc));

        long long f1=1,f2=1;

        int i= m-2,j=n-2;

        long long ans=0;

        while(i>=0 and j>=0){
            // cout<<"ans : "<<ans<<endl;
            if(hc[i]>=vc[j]){
                ans += f1*hc[i]*1L;
                i--;
                f2++;
            }
            else{
                ans += f2*vc[j]*1L;
                j--;
                f1++;
            }
        }
        while(i>=0){
            // cout<<"ans2 : "<<ans<<endl;
            ans += f1*hc[i]*1L;
            i--;
        }
        while(j>=0){
            // cout<<"ans3 : "<<ans<<endl;
            ans += f2*vc[j]*1L;
            j--;
        }

        return ans;
    }
};
