int trie[MAXN][26];
int ids[MAXN];
int cur;
void add(string &s, int id) {
  int p{0};
  for(auto e: s) {
    auto c = e-'a';
    if (!trie[p][c]) {
      trie[p][c] = ++cur;
    }
    p = trie[p][c];
  }
  ids[p] = ids[p] ? min(ids[p],id) : id;
}

int qry(string &s) {
  int p{0};
  int ans{0};
  for(auto e: s) {
    auto c = e-'a';
    if (!trie[p][c]) {
      ans<<=1;
      p=trie[p][!c];
    } else {
      ans=ans<<1|1;
      p=trie[p][c];
    }
  }
  return ans;
}


class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pii> v;
        for(auto e:horizontalCut) {
          v.eb(e,0);
        }
        for(auto e:verticalCut) {
          v.eb(e,1);
        }
        sort(all(v),greater<pii>());
        ll ans = 0;
        int cnt[] = {1,1};
        for(auto [w,d] : v) {
          ans+=(ll)cnt[d]*w;
          cnt[!d]+=1;
        }
        return ans;
    }
};
