class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hCut, vector<int>& vCut) {
        sort(hCut.begin(),hCut.end());
        sort(vCut.begin(),vCut.end());
        // funtion<int(int,int)>solve=[&](int h,int v,int counth,int countr){
        //     if(h<0||v<0) return 0;
        //     if(dp[h][v][counth][countr]!=-1) return dp[h][v][counth][countr];
        //     int a=hcut[h]*countv +solve()
        // };
        int counth=1;
        int countr=1;
        int h=m-2,r=n-2;
        long long res=0;
        while(h>=0&&r>=0){
            if(hCut[h]>vCut[r]){
                res+=hCut[h]*countr;
                counth++;
                h--;
            }
            else{
                res+=vCut[r]*counth;
                countr++;
                r--;
            }
        }
        while(h>=0){
             res+=hCut[h]*countr;
            h--;
        }
         while(r>=0){
             res+=vCut[r]*counth;
            r--;
        }
        return res;
    }
};
