class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());
        int ch = 1, hidx = 0;
        int cv = 1, vidx = 0;
        long long cost = 0;
        while(hidx < horizontalCut.size() && vidx < verticalCut.size()){
            if(horizontalCut[hidx] > verticalCut[vidx]){
                cv++;
                cost += horizontalCut[hidx] * ch;
                hidx++;
            }
            else if(horizontalCut[hidx] < verticalCut[vidx]){
                ch++;
                cost += verticalCut[vidx] * cv;
                vidx++;
            }
            else{
                if(m - hidx > n - vidx){
                    cv++;
                    cost += horizontalCut[hidx] * ch;
                    hidx++;
                }
                else{
                    ch++;
                    cost += verticalCut[vidx] * cv;
                    vidx++;
                }
            }
        }
        while(hidx < horizontalCut.size()){
            cost += horizontalCut[hidx] * ch;
            hidx++;
        }
        while(vidx < verticalCut.size()){
            cost += verticalCut[vidx] * cv;
            vidx++;
        }
        return cost;
    }
};
