class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long int i,ans=0,ch=1,cv=1;
        vector<vector<int>>v;
        for(i=0;i<m-1;i++){
            v.push_back({horizontalCut[i],0});
        }
        for(i=0;i<n-1;i++){
            v.push_back({verticalCut[i],1});
        }
        sort(v.begin(),v.end(),greater<>());
        for(i=0;i<m+n-2;i++){
            if(v[i][1]==0){
                ans+=v[i][0]*cv;
                ch++;
            }
            else{
                ans+=v[i][0]*ch;
                cv++;
            }
        }
        return ans;
    }
};
