class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        long long ans=0;
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
        sort(v.begin(),v.end());
        reverse(v.begin(),v.end());
        int hh=0,vv=0;
        while(hh<m-1 && vv<n-1)
        {
            if(h[hh]>=v[vv])
            {
                ans+=(long long)(vv+1)*h[hh];
                hh++;
            }
            else
            {
                ans+=(long long)(hh+1)*v[vv];
                vv++;
            }
        }
        while(hh<m-1)
        {
            ans+=(long long)(vv+1)*h[hh];
            hh++;
        }
        while(vv<n-1)
        {
            ans+=(long long)(hh+1)*v[vv];
            vv++;
        }
        return ans;
    }
};
