class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<long long, long long>> v;
        for(long long it:horizontalCut) {
            v.push_back({it, 0});
        }
        for(long long it:verticalCut) {
            v.push_back({it, 1});
        }

        sort(v.rbegin(), v.rend());

        long long hCnt = 0, vCnt = 0;

        long long cost = 0;

        for(long long i=0; i<m+n-2; i++) {
            cost += (v[i].second==0) ? v[i].first * (vCnt+1) : v[i].first * (hCnt+1);
            if(v[i].second==1) {
                vCnt++;
            } else {
                hCnt++;
            }
        }

        return cost;
    }
};
