class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& a, vector<int>& b) {
          vector<pair<long long,long long>>v;
             for(int i=0;i<a.size();i++){
                   v.push_back({a[i],0});
             }
    for(int i=0;i<b.size();i++){
          v.push_back({b[i],1});
    }
    sort(v.begin(),v.end());
    reverse(v.begin(),v.end());
    long long ans=0;
    long long x=1,y=1;
    for(int i=0;i<v.size();i++){
        if(v[i].second==0){
              ans+=(y*v[i].first);
              x++;
        }
        else {
              ans+=(x*v[i].first);
             y++;
        }
    }
    return ans;
    }
};
