class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<int>& hor=horizontalCut;
        vector<int>& ver=verticalCut;
        long long hsum=accumulate(hor.begin(),hor.end(),0ll);
        long long vsum=accumulate(ver.begin(),ver.end(),0ll);
        long long hcut=1,vcut=1,ans=0;
        int i=0,j=0;
        sort(hor.rbegin(),hor.rend());
        sort(ver.rbegin(),ver.rend());
        while(i<m-1 && j<n-1)
        {
            if(hor[i]>ver[j])
            {
                ans+=1ll*hor[i]*vcut;
                i++;
                hcut++;
            }else{
                ans+=1ll*ver[j]*hcut;
                j++;
                vcut++;
            }
        }
        while(i<m-1){
            ans+=1ll*hor[i]*vcut;
            i++;
            hcut++;
        }
        while(j<n-1){
            ans+=1ll*ver[j]*hcut;
            j++;
            vcut++;
        }
        return ans;
    }
};
