class Solution {
public:
    using ll = long long;

    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        ll res = 0;

        sort(hor.rbegin(), hor.rend());
        sort(ver.rbegin(), ver.rend());

        int horCnt = 1, verCnt = 1;
        int i = 0, j = 0;

        for (; i < m - 1 && j < n - 1;) {
            if (hor[i] > ver[j]) {
                res += static_cast<ll>(hor[i]) * verCnt;
                horCnt++;
                i++;
            } else {
                res += static_cast<ll>(ver[j]) * horCnt;
                verCnt++;
                j++;
            }
        }

        for (; i < m - 1; i++) {
            res += static_cast<ll>(hor[i]) * verCnt;
        }

        for (; j < n - 1; j++) {
            res += static_cast<ll>(ver[j]) * horCnt;
        }

        return res;
    }
};
