using ll = long long;
class Solution
{
public:
    ll minimumCost(int m, int n, vector<int> &hc, vector<int> &vc, ll ans = 0)
    {
        sort(begin(hc), end(hc), greater<int>());
        sort(begin(vc), end(vc), greater<int>());

        ll hi = 0, vi = 0, hp = 1, vp = 1;
        while (hi < size(hc) and vi < size(vc))
        {
            if (hc[hi] >= vc[vi]) ans += hc[hi++] * vp,hp++;
            else ans += vc[vi++] * hp,vp++;
        }

        while (hi < size(hc)) ans += hc[hi++] * vp;
        while (vi < size(vc)) ans += vc[vi++] * hp;

        return ans;
    }
};
